import React, { useContext } from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import MyContext from '../context/MyContext';
import '../css/Projects.css';

function ProjectCard(props) {
  const { imagePath, id, name } = props;
  const { getProjectById } = useContext(MyContext);

  const handleClick = ({ target: { id } }) => {
    console.log('chegou aqui');
    getProjectById(id)
  };

  return(
    <div className="project-item" id={id}>
      <div className="project-item-inner outer-shadow">
        <div className="project-item-img">
          <img src={imagePath} alt="profile" data-screenshots="img"/>
          <Link to={`/ProjectDetails/${id}`} className="view-project" onClick={(e) => handleClick(e)} >
            view project
          </Link>
        </div>
        <p className="project-item-title">{name}</p>
      </div>
    </div>
  );
}

ProjectCard.propTypes = {
  imagePath: PropTypes.string,
  id: PropTypes.number,
  name: PropTypes.string,
}.isRequired;

export default ProjectCard;
