import React, { useState } from 'react';
import { Projects, icons } from '../Data'
import MyContext from './MyContext';
import PropTypes from 'prop-types';

function Provider({ children }) {
  const INITIAL_STATE = {
    id: 1,
    name: 'Project-1',
    category: 'frontend',
    imagePath: '../pictures/profile.jpg',
    detail: 'lalalallalalalalalallala',
    tools: 'js, node, react',
    date: '01/01/1999',
    link: 'www.exemplo',
  };
  const [projectById, setProjectById] = useState(INITIAL_STATE);
  const [projectData] = useState(Projects);
  const [iconsData] = useState(icons);

  const getProjectById = (id) => {
    const currentProject = projectData.filter((project) => project.id === id);
    setProjectById(currentProject);
  }

  const context = {
    projectById,
    getProjectById,
    projectData,
    iconsData,
  };

  return (
    <MyContext.Provider value={ context }>
    {children}
  </MyContext.Provider>
  );
}

Provider.propTypes = {
  children: PropTypes.node.isRequired,
};

export default Provider;
